package lec1;

public class odd_even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n= 671;
        if (n > 781) {
        	 System.out.println("yes");
        }else {
        	 System.out.println("no");
        }
        if (n%2 == 0) {
        	 System.out.println("even");
        }
        else {
        	 System.out.println("odd");
        }
	}

}
