package lec2;

public class plus_sign {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int x =7;
        System.out.println(x++);
        System.out.println(x);
        int a = 9;
       System.out.println(++a);
       System.out.println(a);
       int p=7;
       int b=p++ + p-- +7;
       System.out.println(b);
       
      	   
       
     
        
        		
	}

}

