package lec2;

public class sum_of_n_numbers_using_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n=10;
        int i = 1;
        int sum=0;
        while(i<=n) {
        	sum=sum+i;
        	i++;
        }System.out.println(sum);
        			
        }
        
	}


